ulo = $_SESSION['usuario']." ha contestado en el caso  ".$_POST['id_caso'];				$cuerpo = $_SESSION['usuario']." ha agregado un comentario en el caso ".$_POST['id_caso'].":<br /><br />".$_POST['respuesta']."<br /><br />para verlo de click <a href='http://sistemas.tpitic.com.mx/HelpDesk/php/mostrar_caso_detalles.php?idcaso=".$_POST['id_caso']."'>Aqui</a>";				$tipo = "comentario";				$objFN->enviarMail($titulo, $cuerpo, $row['ASIGNADO_A'], $tipo);				if($row['USUARIO'] != 'acota'){					$objFN->enviarMail($titulo, $cuerpo, $row['USUARIO'], $tipo);				}			}else{				echo "NO SE ENCONTRO DESTINATARIO";			}		}		$query = "SELECT * FROM casos_respuestas WHERE ID_CASO = '".$_POST['id_caso']."' ORDER BY FECHA ASC";		$result = $mysql->query($query);		$respuestas = "<table style='width:100%;'>";		while($row = mysql_fetch_array($result)){			$respuestas .= "<tr><td><span style='color:red;'>".$row['USUARIO']."</span><br/>".nl2br ($row['RESPUESTA'])."<br /></td></tr>";		}		echo $respuestas .= "</table>";	}else{		echo "Error al grabar la respuesta en la base de datos!";	}}/*function obtenerRespuestas($idCaso){	$mysql = new MySQL();	$mysql->connect(); 	$query = "SELECT * FROM casos_respuestas WHERE ID_CASO = '".$idCaso."' ORDER BY FECHA ASC";	$result = $mysql->query($query);	$respuestas = "<table style='width:100%;'>";	while($row = mysql_fetch_array($result)){		$respuestas .= "<tr><td><span style='color:red;'>".$row['USUARIO']."</span><br/>".$row['RESPUESTA']."<br /></td></tr>";	}	echo $respuestas .= "</table>";}*/?>